import kaboom from "kaboom";

kaboom({
  width: 512,
  height: 320,
  letterbox: false,
  scale: 1.5,
  font: "jersey",
});

import { load } from "./utils/loader";

load.fonts();
load.items();
load.player();
load.sounds();
load.terrain();
load.traps();

export let currentLevel = 1;
export let score = 0;
export const FRUIT_SCORES = {
  apple: 1,
  banana: 1,
  cherry: 2,
  kiwi: 2,
  orange: 3,
  pineapple: 3,
  strawberry: 4,
  watermelon: 5,
};

level.get("fruits").forEach((fruit) => {
  const fruits = Object.keys(FRUIT_SCORES);
  const randomFruit = fruits[Math.floor(Math.random() * fruits.length)];
  fruit.name = randomFruit;
  fruit.play(randomFruit);
});

player.onCollide("fruits", async (fruit) => {
  play("coin");
  fruit.play("collected");
  await wait(0.2);
  destroy(fruit);
  score += FRUIT_SCORES[fruit.name];
  currentScore.text = `Score: ${score}`;
});

def get_diff_sum(values: list[int]) -> int:
    """Return the sum of the extrapolated values.

    :return int: sum of extrapolated values
    """
    diff = [b - a for a, b in zip(values, values[1:])]
    while any(diff):
        return values[-1] + get_diff_sum(diff)
    return values[-1]


if __name__ == "__main__":
    with open("2023/day_09/input.txt", "r") as file:
        data = file.read().splitlines()

    data = [[*map(int, line.split())] for line in data]
    print(f"Sum of the extrapolated values: {sum(map(get_diff_sum, data))}")

def get_diff_sum(values: list[int]) -> int:
    """Return the sum of the extrapolated values (backwards).

    :return int: sum of extrapolated values
    """
    diff = [b - a for a, b in zip(values, values[1:])]
    while any(diff):
        return values[0] - get_diff_sum(diff)
    return values[0]


if __name__ == "__main__":
    with open("2023/day_09/input.txt", "r") as file:
        data = file.read().splitlines()

    data = [[*map(int, line.split())] for line in data]
    print(f"Sum of the extrapolated values (backwards): {sum(map(get_diff_sum, data))}")
